mapper.select:
SELECT * FROM card


        app_id = #{appId}


        AND (card_code LIKE CONCAT('%', #{keyword}, '%'))


        AND status = #{status}


ORDER BY id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(1) FROM card


        app_id = #{appId}


        AND (card_code LIKE CONCAT('%', #{keyword}, '%'))


        AND status = #{status}

mapper.update:
UPDATE card
SET status = #{status},
    activated_at = COALESCE(activated_at, #{activatedAt}),
    updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE card
SET status = #{status},
    activated_at = COALESCE(activated_at, #{activatedAt}),
    expire_at = COALESCE(expire_at, #{expireAt}),
    updated_at = #{updatedAt}
WHERE id = #{id}
